self.addEventListener('message', (event) => {
  if (event.data && event.data.type === 'SKIP_WAITING') {
    self.skipWaiting();
  }
});

self.addEventListener('install', (event) => {
  console.log('M1SSIONâ„¢ Service Worker installed');
  self.skipWaiting();
});

self.addEventListener('activate', (event) => {
  console.log('M1SSIONâ„¢ Service Worker activated');
  event.waitUntil(self.clients.claim());
});

// Push notification handler - iOS compatible
self.addEventListener('push', (event) => {
  console.log('ðŸ”” SW: Push event received:', event);
  console.log('ðŸ”” SW: Event data available:', !!event.data);
  
  if (!event.data) {
    console.log('ðŸ”” SW: No data in push event - showing fallback notification');
    event.waitUntil(
      self.registration.showNotification('M1SSIONâ„¢', {
        body: 'Nuova notifica disponibile',
        icon: '/favicon.ico',
        badge: '/favicon.ico',
        requireInteraction: true,
        data: { url: '/notifications' },
        tag: 'mission-fallback'
      })
    );
    return;
  }
  
  try {
    const data = event.data.json();
    console.log('ðŸ”” SW: Push data parsed:', data);
    
    // âœ… CRITICAL FIX: Handle both direct payload and nested data structures
    const notificationData = data.notification || data;
    const title = notificationData.title || data.title || 'M1SSIONâ„¢';
    const body = notificationData.body || data.body || 'Nuova notifica da M1SSIONâ„¢';
    
    console.log(`ðŸ”” SW: Extracted - Title: "${title}", Body: "${body}"`);
    
    const options: NotificationOptions = {
      body: body,
      icon: '/favicon.ico',
      badge: '/favicon.ico',
      data: {
        url: '/notifications',
        timestamp: new Date().toISOString(),
        source: 'push_notification',
        ...data.data
      },
      tag: data.tag || 'mission-notification',
      silent: false,
      requireInteraction: true, // iOS compatibility
      dir: 'ltr' as NotificationDirection,
      lang: 'it'
    };

    console.log('ðŸ”” SW: Showing notification with options:', options);
    
    event.waitUntil(
      self.registration.showNotification(title, options)
        .then(() => {
          console.log('ðŸ”” SW: Notification shown successfully');
          // âœ… Send message to main thread to update UI
          self.clients.matchAll().then(clients => {
            clients.forEach(client => {
              client.postMessage({
                type: 'PUSH_NOTIFICATION_RECEIVED',
                data: { title, body, timestamp: new Date().toISOString() }
              });
            });
          });
        })
        .catch((error) => {
          console.error('ðŸ”” SW: Error showing notification:', error);
        })
    );
  } catch (error) {
    console.error('ðŸ”” SW: Push notification parsing error:', error);
    // Fallback notification with iOS compatibility
    event.waitUntil(
      self.registration.showNotification('M1SSIONâ„¢', {
        body: 'Nuova notifica disponibile',
        icon: '/favicon.ico',
        badge: '/favicon.ico',
        requireInteraction: true,
        data: { url: '/notifications' },
        tag: 'mission-fallback'
      })
    );
  }
});

// Notification click handler - iOS compatible
self.addEventListener('notificationclick', (event) => {
  console.log('ðŸ”” SW: Notification clicked:', event);
  
  event.notification.close();
  
  const urlToOpen = event.notification.data?.url || '/notifications';
  console.log('ðŸ”” SW: Opening URL:', urlToOpen);
  
  event.waitUntil(
    self.clients.matchAll({
      type: 'window',
      includeUncontrolled: true
    }).then((clientList) => {
      console.log('ðŸ”” SW: Found clients:', clientList.length);
      
      // Check if app is already open
      for (const client of clientList) {
        if (client.url.includes(self.location.origin) && 'focus' in client) {
          console.log('ðŸ”” SW: Focusing existing client and navigating to:', urlToOpen);
          client.navigate(urlToOpen);
          return client.focus();
        }
      }
      
      // Open new window if app not open
      if (self.clients.openWindow) {
        console.log('ðŸ”” SW: Opening new window:', urlToOpen);
        return self.clients.openWindow(urlToOpen);
      }
    }).catch((error) => {
      console.error('ðŸ”” SW: Error handling notification click:', error);
    })
  );
});